import React from 'react'
import { ErrorMessage} from 'formik'

export const Textfield=({label,...props})=> {

    const [field,meta] =  userField(props);
    return (
        <div className="mb-2">
        <label htmlfor={field.name}></label>
        <input 

            className={`form-control shadow-none ${meta.touched && meta.error && 'is-invalid'}`} {...field} {...props} autoComplete="off"
        />

        <ErrorMessage component="div" name={field.name} className="error"></ErrorMessage>
            
        </div>
    )
}

export default Textfield
